import './LoginPage.css';

import jwtDecode from 'jwt-decode';
import { useEffect, useRef,useState } from 'react';



const loadScript = (src) =>
new Promise((resolve, reject) => {
  if (document.querySelector(script[src="${src}"])) return resolve()
  const script = document.createElement('script')
  script.src = src
  script.onload = () => resolve()
  script.onerror = (err) => reject(err)
  document.body.appendChild(script)
})


// #region constants

// #endregion

// #region styled-components

// #endregion

// #region functions

// #endregion

// #region component
const propTypes = {};

const defaultProps = {};

/**
 * 
 */
const LoginPage = () => {

    const[user,setUser]=useState({});

    const googleButton = useRef(null);
  
    useEffect(() => {
      const src = 'https://accounts.google.com/gsi/client'
      const id = "420031286914-2us2kap97h30euaka61e5ld5fq4vh7s6.apps.googleusercontent.com"
  
      loadScript(src)
        .then(() => {
          /*global google*/
          console.log(google)
          google.accounts.id.initialize({
            client_id: id,
            callback: handleCredentialResponse,
          })
          google.accounts.id.renderButton(
             
            googleButton.current, 
            { theme: 'outline', size: 'large' } 
          )
        })
        .catch(console.error)
  
    }, [])
  
    function handleCredentialResponse(response) {
      console.log("Encoded JWT ID token: " + response.credential);
      var userObject=jwtDecode(response.credential);
      console.log(userObject);
      console.log(userObject);
      setUser(userObject);
      document.getElementById('signInDive').hidden=true;
    }
  
    function handleSignOut(event){
      setUser({});
      document.getElementById( 'signInDive').hidden=false;
    }
    return <div>

    
   
  

<div ref={googleButton} id='signInDive'></div>
    </div>;
}

LoginPage.propTypes = propTypes;
LoginPage.defaultProps = defaultProps;
// #endregion

export default LoginPage;
